import axios from 'axios'
import Vue from "vue";

const instance = axios.create({
	baseURL: 'http://localhost:8080/', // api 的 base_url
	// withCredentials: false//跨域时使用凭证，默认带上cookies
	// timeout: 2000, // request timeout  设置请求超时时间
  })

//添加请求拦截器
instance.interceptors.request.use(
  config =>{
    config.headers.common['token'] = "test";
    if(window.sessionStorage.getItem("CCNtoken")!=null){
      config.headers['token'] =window.sessionStorage.getItem("CCNtoken")
    }
    return config;
  },
  error =>{
    //对请求错误做什么
    return Promise.reject(error);
  });
  Vue.prototype.$http = axios
  export default instance
