import Vue from 'vue'
import App from './App.vue'
import LoginPage from './components/LoginPage.vue'
import Register from './components/Register.vue'
import VueRouter from 'vue-router';
import axios from 'axios'

Vue.use(VueRouter);

const routes = [
  {
    path: '/',
    name: 'LoginPage',
    component: LoginPage
  },
  {
    path: '/register',
    name: 'register',
    component: LoginPage
  },
];

const router = new VueRouter({
  routes // (缩写) 相当于 routes: routes
})

router.beforeEach((to, from, next) => {
  if (to.path === '/login') {
    next();
  } else {
    let token = window.sessionStorage.getItem("token")?window.sessionStorage.getItem("token"):null
    if (token === null || token === '') {
      next('/login');
    } else {
      next();
    }
  }
});


const instance = axios.create({
	baseURL: 'http://localhost:8080/', // api 的 base_url
	withCredentials: false//跨域时使用凭证，默认带上cookies
	// timeout: 2000, // request timeout  设置请求超时时间
  })

//添加请求拦截器
instance.interceptors.request.use(
  config =>{
    if(window.sessionStorage.getItem("CCNtoken")!=null){
      config.headers.common['token'] =window.sessionStorage.getItem("CCNtoken")
    }
    return config;
  },
  error =>{
    //对请求错误做什么
    return Promise.reject(error);
  })
  export default instance


new Vue({

  render: h => h(App),
  router

}).$mount('#app')
