import Vue from "vue";
import VueRouter from 'vue-router'
import login from "../components/LoginPage.vue";
//2.使用路由
Vue.use(VueRouter);
//3.创建VueRouter的实例
const router = new VueRouter({
    //tips:不想要 #（锚点）就添加下面代码
     mode:'history', 
    //4.配置路由的path和组件
    routes :[
        {
          path: "/",
          name:'home',
          component: login,
        },
        {
          path: "/login",
          name:'home',
          component: login,
        },
        {
          path: "/register",
          name:'register',
          component: () => import("../components/RegisterPage.vue"),
        },
      ]
})

router.beforeEach((to, from, next) => {
  if (to.path === '/login') {
    next();
  } else {
    let token = window.sessionStorage.getItem("token")
    if (token === null || token === '') {
      next('/');
    } else {
      next();
    }
  }
});
// 5.导入路由实例
export default router
