import Vue from 'vue'
import App from './App.vue'
import VueRouter from 'vue-router';
import axios from 'axios'

// Vue.prototype.$http = axios;
Vue.use(VueRouter);
// Vue.config.productionTip = false

// const routes = [

//   {
//     path: '/',
//     name: 'home',
//     component: App
//   },
// ];

// axios.defaults.baseURL = "http://localhost:8080/"
//添加请求拦截器
axios.interceptors.request.use(
  config =>{
    config.headers['token']="11111"
    config.headers['Referer']="https://www.bilibili.com"
    // if(window.sessionStorage.getItem("CCNtoken")!=null){
    //   config.headers.common['token'] =window.sessionStorage.getItem("CCNtoken")
    // }
    return config;
  },
  error =>{
    //对请求错误做什么
    return Promise.reject(error);
  })

new Vue({
  render: h => h(App),

}).$mount('#app')
