import Vue from "vue";
import VueRouter from 'vue-router'
import login from "../components/LoginPage.vue";
import register from "../components/RegisterPage.vue";
import index from "../components/IndexPage.vue";
// import TableCom from "../components/TableCom.vue";
// import Project from "../components/ProjectPage.vue";
import projectsBox from "../components/ProjectsBox.vue";
import projectPage from "../components/projectPage.vue";

import introPage from "../components/IntroPage.vue";
import msgPage from "../components/MsgPage.vue";
import codePage from "../components/codePage.vue";
//2.使用路由
Vue.use(VueRouter);

//3.创建VueRouter的实例
const router = new VueRouter({
    //tips:不想要 #（锚点）就添加下面代码
     mode:'history', 
    //4.配置路由的path和组件
    routes :[
        {
          path: "/",
          name:'login',
          component: login,
        },
        {
          path: "/login",
          name:'login',
          component: login,
        },
        {
          path: "/register",
          name:'register',
          component: register,
        },
        {
          path: "/home",
          name:'index',
          component: index,
          children:[{
            path: "projectsBox",
            name:'projectsBox',
            component:projectsBox,
          },
          {
            path:"projectPage",
            name:"projectPage",
            component:projectPage,
            children:[
              {
                path: "code",
                name:'code',
                component:codePage,
              },
              {
                path: "msg",
                name:'msg',
                component:msgPage,
              },
              {
                path: "setting",
                name:'settings',
                component:codePage,
              },
              {
                path: "intro",
                name:'intro',
                component:introPage,
              },
              
            ]
          },
          // {
          //   path:"Project",
          //   name:"Project",
